# inventory_ids = [inventory_ids[0]]
for id in inventory_ids: 
	id_lookup = {}
	for char in id: 
		if char in id_lookup: 
			id_lookup[char] += 1 
		else: 
			id_lookup[char] = 1  

	two_count = False 
	three_count = False 
	for char in id_lookup: 
		two_count = two_count or id_lookup[char] == 2
		three_count = three_count or id_lookup[char] == 3 

	if two_count: total_two_count += 1  
	if three_count: total_three_count += 1  

