# Part 2 
"""
for each id:
	valid_matches = []  
	for each id2: 
		for idx in range(len(id))
"""

correct_answer = None
for idx in range(len(inventory_ids)): 
	for idx2 in range(len(inventory_ids)): 
		# Skip identical ids 
		if idx == idx2: 
			continue 
		 
		# Evaluate the two ids for a match. 
		error = False
		valid = True 
		for char_idx in range(len(inventory_ids[idx])): 
			if inventory_ids[idx][char_idx] != inventory_ids[idx2][char_idx]: 
				if error == True:
					valid = False
					break
				error = True 

		# Break if valid
		if valid:
			correct_answer = [idx, idx2]
			break

	# Break if correct_answer found
	if correct_answer != None: 
		break

if correct_answer != None: 
	print(inventory_ids[correct_answer[0]])
	print(inventory_ids[correct_answer[1]])
else: 
	raise Exception("correct_answer was not found")

